<template>  <v-row justify="center" style="margin: auto 40px">    <v-col cols="2"></v-col>    <v-col      cols="2"      style="position: fixed; left: 35px;"      v-show="width >= 1264"    >      <v-skeleton-loader        v-show="firstload"        type="list-item-two-line@2"      ></v-skeleton-loader>      <v-hover>        <template v-slot:default="{ hover }">          <v-stepper            :class="`elevation-${hover ? 8 : 2}`"            class="transition-swing"            v-show="!firstload"            v-model="position"            vertical            non-linear            style="transition: all 1s ease 1s; padding-bottom: 8px"          >            <v-stepper-step              editable              edit-icon="1"              step="1"              @click="$vuetify.goTo('#prologue', options)"              :complete="position > 1"              style="transition: all 1s ease 1s"              color="primary"              class="body-1"            >              Prologue            </v-stepper-step>            <v-stepper-step              editable              edit-icon="2"              step="2"              @click="$vuetify.goTo('#lab_safety', options)"              :complete="position > 2"              style="transition: all 1s"              color="primary"              class="body-1"            >              Lab Safety            </v-stepper-step>            <v-stepper-step              editable              edit-icon="3"              step="3"              @click="$vuetify.goTo('#general_information', options)"              :complete="position > 3"              style="transition: all 1s"              color="primary"              class="body-1"            >              General microorganism information & general project design            </v-stepper-step>            <v-stepper-step              editable              edit-icon="4"              step="4"              @click="$vuetify.goTo('#specific_design